sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var v_res= inputs.response;
  var v_resObj= JSON.parse(v_res);
  outputs.id=v_resObj[0].id;
  outputs.name=v_resObj[0].filename;
  outputs.url=v_resObj[0].self;
})(inputs, outputs);

